	var nav = document.getElementById("navgt");
	var navLists = nav.getElementsByClassName("dot");
	var navItems = nav.getElementsByTagName("a");
	for(var i=0;i<navItems.length;i++){
		navItems[i].addEventListener("click",function(e){
			var _a = e.target;
			for(var j=0;j<navLists.length;j++){
				navLists[j].className="dot";
			}
			_a.parentNode.className="dot active";
		},false);
	}
	
$(function(){ 
$("#navgt").scrollTo(800)
});

(function($){
	$.extend($.fn,{
		scrollTo:function(time,to){
			time=time||800;
			to=to||1;
            $('a[href*=#]', this).click(function(){
                if (location.pathname.replace(/^\//, '') == this.pathname.replace(/^\//, '') && location.hostname == this.hostname) {
                    var $target = $(this.hash);
                    $target = $target.length && $target || $('[name=' + this.hash.slice(1) + ']');
                    if ($target.length) {
                        if (to == 1) {
                            $('html,body').animate({
                                scrollTop: $target.offset().top
                            }, time);
                        }
                        else if(to==2){
                            $('html,body').animate({
                                scrollLeft: $target.offset().left
                            }, time);
                        }else{
							alert('argument errorï¼');
						}
                        return false;
                    }
                }
            });
		}
	});
})(jQuery);